class SyncWaterfallHook_MY {
  constructor(args) {
    this.hook = new Map([])
    this.args = args
  }

  tap(name, fn) {
    this.hook.set(name, fn);
  }

  call(...args) {
    let argAfter = args[0];
    [...this.hook.values()].forEach((_fn) => {
      let _argAfter = _fn(argAfter, ...args.slice(1, this.args.length));
      if (_argAfter) {
        argAfter = _argAfter;
      }
    });
    return argAfter;
  }
}
