function generateParenthesis(n) {
  // \u5361\u7279\u5170\u6570
  // \u9012\u63A8\u516C\u5F0F\uFF1Ah(n) = h(0)*h(n-1)+h(1)*h(n-2)...+h(n-1)*h(0)

  let f = [1, 1]
  for (let i = 2; i < n; i++) {
    // \u8FD9\u91CC\u7684i\u8868\u793A\u6BCF\u4E00\u9879\u8BA1\u7B97\u7684\u4E0B\u6807\uFF0C\u5982i=5\uFF0C\u7ED3\u679C\u4E3A42
    let temp = 0
    for (let j = 0; j < i; j++) {
      temp += f[j] * f[i - j - 1]
    }
    f[i] = temp
  }
  return f.pop()
}

