function workLoop(deadline){
  while(nextFiber && !shouldYield){
          nextFiber = performUnitOfWork(nextFiber)
          shouldYield = deadline.timeReaming < 1 // \u4E0D\u7A7A\u95F2\uFF0C\u5173\u95EDshouldYield
  }
  requestIdleCallback(workLoop)
}
   
