//\uFF081\uFF09\u5728\u6570\u636E\u96C6\u4E4B\u4E2D\uFF0C\u9009\u62E9\u4E00\u4E2A\u5143\u7D20\u4F5C\u4E3A"\u57FA\u51C6"\uFF08pivot\uFF09\u3002
