// AsyncSeriesHook
class AsyncSeriesHook_MY {
  constructor(args) {
    this.hook = new Map([])
    this.args = args
  }

  tapPromise(name, fn) {
    this.hook.set(name, fn);
  }

  promise(...args) {
    let result = Promise.resolve(); // result \u5B58\u50A8\u4E00\u4E2Apromise\u7C7B\u578B\u3002
    [...this.hook.values()].forEach((_fn) => {
      // \u91CD\u70B9\uFF0C\u4F7F\u7528\u5FAA\u73AF\uFF0C\u5D4C\u5957then\uFF0C\u5B9E\u73B0\u4E32\u884C\u5F02\u6B65\u51FD\u6570\u3002
      result = result.then ?
        result.then(() =>
            _fn(...args.slice(0, this.args.length))
        ) :
        Promise.resolve();
    })
    return result;
  }
}
