this.addEventListener('activate', event => {
  console.log('activate')
  const whiteList = [CACHE_NAME]
  event.waitUntil(
    caches.keys().then(cacheNames => {
      // \u5220\u9664\u4E0A\u4E00\u4E2A\u7248\u672C\u7684\u7F13\u5B58\u6570\u636E\uFF0C\u4FDD\u5B58\u4E00\u4E2A\u7248\u672C\u7684\u6570\u636E\u3002
      return Promise.all(
        cacheNames.map(name => {
          if (!whiteList.includes(name)) {
            return caches.delete(name)
          }
        })
      )
    })
  )
})
