// Be attentive to back-pressure.
function writeOneMillionTimes(writer, data, encoding, callback) {
  let i = 1000000;
  write();
  function write() {
    let ok = true;
    do {
      i--;
      if (i === 0) {
        // Last time!
        writer.write(data, encoding, callback);
      } else {
        // \u770B\u770B\u6211\u4EEC\u662F\u8BE5\u7EE7\u7EED\uFF0C\u8FD8\u662F\u7B49\u4E00\u7B49\u3002
        // \u4E0D\u8981\u4F20\u9012\u56DE\u8C03\uFF0C\u56E0\u4E3A\u6211\u4EEC\u8FD8\u6CA1\u5B8C\u6210\u3002
        ok = writer.write(data, encoding);
      }
    } while (i > 0 && ok);
    if (i > 0) {
      // \u4E0D\u5F97\u4E0D\u63D0\u524D\u505C\u4E0B\u6765
      // \u4E00\u65E6\u8017\u5C3D\uFF0C\u518D\u5199\u4E00\u4E9B\u3002
      writer.once('drain', write);
    }
  }
} 
