`,paraId:39,tocIndex:8},{value:"\u7136\u540E\uFF0C\u5B9A\u4E49\u8FD9\u4E2A\u7EC4\u4EF6\u7684 Reducer\u3002",paraId:40,tocIndex:8},{value:`// Reducer
function counter(state = { count: 0 }, action) {
  const count = state.count
  switch (action.type) {
    case 'increase':
      return { count: count + 1 }
    default:
      return state
  }
}
