function generateParenthesis(n: number): string[] {
    const res: string[] = []
    const path: string[] = [];
    const dfs = (left, right) => {
        if (left === 0 && right === 0) {
            return res.push(path.join(''))
        }
        if (left > 0) {
            path.push('(')
            dfs(left - 1, right)
            path.pop()
        }
        if (left < right) {
            path.push(')')
            dfs(left, right - 1);
            path.pop()
        }
    }
    dfs(n,n)
    return res
}

